data.command:
try {
  if (!["mwak"].includes(args[0].item.data.actionType)) return {};
  if (args[0].hitTargetUuids.length === 0) return {}; // did not hit anyone
  for (let tokenUuid of args[0].hitTargetUuids) {
    const target = await fromUuid(tokenUuid);
    const targetActor = target.actor;
    if (!targetActor) continue;
    const spellDC = actor.data.flags["midi-qol"].thunderousSmite.dc;
    ChatMessage.create({ content: `${targetActor.name} needs to make a ${CONFIG.DND5E.abilities["str"]} DC${spellDC} vs Thunderous Smite Stagger.` });

    const saveRollData =  {
      request: "save",
      targetUuid: target.actor.uuid,
      ability: "str",
      options: {
        chatMessage: true,
        flavor: `${CONFIG.DND5E.abilities["str"]} DC${spellDC} vs Thunderous Smite Stagger`,
      },
    };

    // const saveRoll = await targetActor.rollAbilitySave("str", { flavor });
    const saveRoll = await MidiQOL.socket().executeAsGM("rollAbility", saveRollData);

    if (saveRoll.total < spellDC) {
      game.dfreds.effectInterface.addEffect({ effectName: "Prone", uuid: tokenUuid });
      ChatMessage.create({ content: `${targetActor.name} has failed the save and is pushed back 10ft and knocked prone.` });
    }
  }
  Hooks.once("midi-qol.RollComplete", (workflow) => {
    console.log("Deleting concentration");
    const effect = MidiQOL.getConcentrationEffect(actor);
    if (effect) effect.delete();
    return true;
  });
  return { damageRoll: `2d6[thunder]`, flavor: "Thunderous Smite" };
} catch (err) {
  console.error(`${args[0].itemData.name} - Thunderous Smite`, err);
}

try {
  if (!["mwak", "rwak"].includes(args[0].item.data.actionType)) return {};
  if (args[0].hitTargetUuids.length === 0) return {}; // did not hit anyone
  for (let tokenUuid of args[0].hitTargetUuids) {
    const target = await fromUuid(tokenUuid);
    const targetActor = target.actor;
    if (!targetActor) continue;
    // remove the invisible condition
    const effect = targetActor?.effects.find((ef) => ef.data.label === game.i18n.localize("midi-qol.invisible"));
    if (effect)
      await MidiQOL.socket().executeAsGM("removeEffects", { actorUuid: targetActor.uuid, effects: [effect.id] });
    // create the dim light effect on the target
    let bsEffect = new ActiveEffect({
      label: "Branding Smite",
      icon: "systems/dnd5e/icons/spells/enchant-royal-3.jpg",
      changes: [
        {
          value: 5,
          mode: CONST.ACTIVE_EFFECT_MODES.UPGRADE,
          priority: 20,
          key: "ATL.dimLight",
        },
      ],
      duration: { seconds: 60 },
    });
    // 60 seconds is wrong - should look for the branding smite effect and use the remaining duration - but hey

    await MidiQOL.socket().executeAsGM("createEffects", {
      actorUuid: targetActor.uuid,
      effects: [bsEffect.toObject()],
    });
  }
  Hooks.once("midi-qol.RollComplete", (workflow) => {
    console.log("Deleting concentration");
    const effect = MidiQOL.getConcentrationEffect(actor);
    if (effect) effect.delete();
    return true;
  });
  const spellLevel = actor.data.flags["midi-qol"].brandingSmite.level;
  return { damageRoll: `${spellLevel}d6[radiant]`, flavor: "Branding Smite" };
} catch (err) {
  console.error(`${args[0].itemData.name} - Branding Smite`, err);
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const DAEItem = lastArg.efData.flags.dae.itemData;

function valueLimit(val, min, max) {
  return val < min ? min : val > max ? max : val;
}

/**
 * Select for weapon and apply bonus based on spell level
 */
if (args[0] === "on") {
  const weapons = targetActor.items.filter((i) => i.data.type === "weapon");
  let weapon_content = "";

  //Filter for weapons
  weapons.forEach((weapon) => {
    weapon_content += `<label class="radio-label">
  <input type="radio" name="weapon" value="${weapon.id}">
  <img src="${weapon.img}" style="border:0px; width: 50px; height:50px;">
  ${weapon.data.name}
</label>`;
  });

  let content = `
    <style>
    .magicWeapon .form-group {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        align-items: flex-start;
      }

      .magicWeapon .radio-label {
        display: flex;
        flex-direction: column;
        align-items: center;
        text-align: center;
        justify-items: center;
        flex: 1 0 25%;
        line-height: normal;
      }

      .magicWeapon .radio-label input {
        display: none;
      }

      .magicWeapon img {
        border: 0px;
        width: 50px;
        height: 50px;
        flex: 0 0 50px;
        cursor: pointer;
      }

      /* CHECKED STYLES */
      .magicWeapon [type=radio]:checked + img {
        outline: 2px solid #f00;
      }
    </style>
    <form class="magicWeapon">
      <div class="form-group" id="weapons">
          ${weapon_content}
      </div>
    </form>
`;

  new Dialog({
    content,
    buttons: {
      ok: {
        label: `Ok`,
        callback: (html) => {
          const itemId = $("input[type='radio'][name='weapon']:checked").val();
          const weaponItem = targetActor.items.get(itemId);
          let copyItem = duplicate(weaponItem);
          const spellLevel = Math.floor(args[1] / 2);
          const bonus = valueLimit(spellLevel, 1, 3);
          const wpDamage = copyItem.data.damage.parts[0][0];
          const verDamage = copyItem.data.damage.versatile;
          DAE.setFlag(targetActor, "magicWeapon", {
            damage: weaponItem.data.data.attackBonus,
            weapon: itemId,
            weaponDmg: wpDamage,
            verDmg: verDamage,
            mgc: copyItem.data.properties.mgc,
          });
          if (copyItem.data.attackBonus === "") copyItem.data.attackBonus = "0";
          copyItem.data.attackBonus = `${parseInt(copyItem.data.attackBonus) + bonus}`;
          copyItem.data.damage.parts[0][0] = wpDamage + " + " + bonus;
          copyItem.data.properties.mgc = true;
          if (verDamage !== "" && verDamage !== null) copyItem.data.damage.versatile = verDamage + " + " + bonus;
          targetActor.updateEmbeddedDocuments("Item", [copyItem]);
        },
      },
      cancel: {
        label: `Cancel`,
      },
    },
  }).render(true);
}

//Revert weapon and unset flag.
if (args[0] === "off") {
  const { damage, weapon, weaponDmg, verDmg, mgc } = DAE.getFlag(targetActor, "magicWeapon");
  const weaponItem = targetActor.items.get(weapon);
  let copyItem = duplicate(weaponItem);
  copyItem.data.attackBonus = damage;
  copyItem.data.damage.parts[0][0] = weaponDmg;
  copyItem.data.properties.mgc = mgc;
  if (verDmg !== "" && verDmg !== null) copyItem.data.damage.versatile = verDmg;
  targetActor.updateEmbeddedDocuments("Item", [copyItem]);
  DAE.unsetFlag(targetActor, "magicWeapon");
}
