let date = SimpleCalendar?.api?.timestampToDate(game.time.worldTime);
if (date.hour >= 0 && date.hour < 12) {
    await stroudDnD.tagging.tokens.toggle("staff", true);
    await stroudDnD.tagging.tokens.toggle("early", true);
    await stroudDnD.tagging.tokens.toggle("late", true);
} else if (date.hour >= 12 && date.hour < 19) {
    await stroudDnD.tagging.tokens.toggle("staff", false);
    await stroudDnD.tagging.tokens.toggle("early", false);
    await stroudDnD.tagging.tokens.toggle("late", true);
}
else {
    await stroudDnD.tagging.tokens.toggle("staff", false);
    await stroudDnD.tagging.tokens.toggle("early", false);
    await stroudDnD.tagging.tokens.toggle("late", false);
}
