function shutDoors() {
  for (let i = 7; i < 10; i++) { 
    stroudDnD.tagging.doors.setState(`K${i}`, CONST.WALL_DOOR_STATES.CLOSED);
    stroudDnD.tagging.lighting.toggle(`K${i}`); 
  }
}

game.audio.preload("modules/sdnd-strahd-adv/images/maps/Areas/K/K10.%20Dining%20Hall/slamming_doors.mp3")
  .then((sound) => sound.play({volume: 1}))
  .then(() => setTimeout(shutDoors, 3200));
