const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const tokenFromUuid = await fromUuid(lastArg.tokenUuid);
const targetToken = tokenFromUuid.data || token;
const DAEitem = lastArg.efData.flags.dae.itemData;

function getShieldName(type) {
  return `Summoned Fire Shield (${type})`;
}

function createShieldItem(type) {
  const damageType = type == "warm" ? "fire" : "cold";
  const img =
    type === "warm" ? "systems/dnd5e/icons/spells/protect-red-3.jpg" : "systems/dnd5e/icons/spells/protect-blue-3.jpg";
  const item = {
    name: getShieldName(type),
    type: "weapon",
    img: img,
    data: {
      source: "Fire Shield Spell",
      activation: {
        type: "special",
        cost: 0,
        condition: "whenever a creature within 5 feet of you hits you with a melee Attack",
      },
      actionType: "other",
      equipped: true,
      damage: {
        parts: [[`2d8[${damageType}]`, damageType]],
      },
      weaponType: "natural",
    },
    effects: [],
  };
  return item;
}

async function createFireShield(type) {
  const resistanceType = type == "warm" ? "cold" : "fire";
  const item = createShieldItem(type);
  const effect = targetActor.effects.find((e) => e.data.label === lastArg.efData.label);
  const changes = [
    {
      key: "data.traits.dr.value",
      mode: CONST.ACTIVE_EFFECT_MODES.ADD,
      priority: 30,
      value: resistanceType,
    },
  ];
  await effect.update({ changes: changes.concat(effect.data.changes) });
  await targetActor.createEmbeddedDocuments("Item", [item]);
  await DAE.setFlag(targetActor, "fireShieldSpell", type);
  ChatMessage.create({ content: `${targetToken.name} gains resistance to ${resistanceType}` });
  ChatMessage.create({ content: `${targetToken.name} has Fire Shield item added to inventory ` });
}

if (args[0] === "on") {
  new Dialog({
    title: "Warm or Cold Shield",
    content: "<p>Choose a shield type</p>",
    buttons: {
      warm: {
        label: "Warm",
        callback: async () => await createFireShield("warm"),
      },
      cold: {
        label: "Cold",
        callback: async () => await createFireShield("cold"),
      },
    },
  }).render(true);
}

if (args[0] === "off") {
  const flag = await DAE.getFlag(targetActor, "fireShieldSpell");
  const item = targetActor.items.getName(getShieldName(flag));
  ChatMessage.create({ content: "Fire Shield expires on " + targetToken.name });
  await DAE.unsetFlag(targetActor, "fireShieldSpell");
  await targetActor.deleteEmbeddedDocuments("Item", [item.id]);
}

const lastArg = args[args.length - 1];
const castItemName = "Summoned Spiritual Weapon";
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;


async function deleteTemplates(actorId) {
  let removeTemplates = canvas.templates.placeables.filter(
    (i) => i.data.flags?.SpiritualWeaponRange?.ActorId === actorId
  );
  await canvas.scene.deleteEmbeddedDocuments("MeasuredTemplate", removeTemplates.map((t) => t.id));
}

/**
 * Create Spiritual Weapon item in inventory
 */
if (args[0] === "on") {
  const tokenFromUuid  = await fromUuid(lastArg.tokenUuid);
  const targetToken = tokenFromUuid.data || token;
  const DAEItem = lastArg.efData.flags.dae.itemData;
  const damage = Math.floor(Math.floor(args[1]/ 2));
  // draw range template
  await canvas.scene.createEmbeddedDocuments("MeasuredTemplate", [
    {
      t: "circle",
      user: game.userId,
      x: targetToken.x + canvas.grid.size / 2,
      y: targetToken.y + canvas.grid.size / 2,
      direction: 0,
      distance: 60,
      borderColor: "#FF0000",
      flags: {
        SpiritualWeaponRange: {
          ActorId: targetActor.id,
        },
      },
    },
  ]);

  const templateData = {
    t: "rect",
    user: game.user.id,
    distance: 7,
    direction: 45,
    x: 0,
    y: 0,
    flags: {
      SpiritualWeapon: {
        ActorId: targetActor.id,
      },
    },
    fillColor: game.user.color,
    texture: DAEItem.img,
  };
  Hooks.once("createMeasuredTemplate", () => deleteTemplates(targetActor.id));
  const doc = new CONFIG.MeasuredTemplate.documentClass(templateData, { parent: canvas.scene });
  let template = new game.dnd5e.canvas.AbilityTemplate(doc);
  template.actorSheet = targetActor.sheet;
  template.drawPreview();

  const castItem = targetActor.data.items.find((i) => i.name === castItemName && i.type === "weapon");
  if (!castItem) {
    const weaponData = {
      name: castItemName,
      type: "weapon",
      data: {
        quantity: 1,
        activation: { type: "bonus", cost: 1, condition: "", },
        target: { value: 1, type: "creature", },
        // we choose 60ft here in case the midi-check range is on
        range: { value: 60, long: null, units: "ft", },
        ability: DAEItem.data.ability,
        attackBonus: DAEItem.data.attackBonus,
        actionType: "msak",
        chatFlavor: "",
        critical: null,
        damage: { parts: [[`${damage}d8+@mod`, "force"]], versatile: "" },
        weaponType: "simpleM",
        proficient: true,
        equipped: true,
      },
      flags: { SpiritualWeapon: targetActor.id },
      img: DAEItem.img,
    };

    await targetActor.createEmbeddedDocuments("Item", [weaponData]);
    ui.notifications.notify("Weapon created in your inventory");
  }
}

// Delete Spiritual Weapon
if (args[0] === "off") {
  let swords = targetActor.data.items.filter((i) => i.data.flags?.SpiritualWeapon === targetActor.id);
  if (swords.length > 0) await targetActor.deleteEmbeddedDocuments("Item", swords.map((s) => s.id));
  const templates = canvas.templates.placeables.filter((i) => i.data.flags?.SpiritualWeapon?.ActorId === targetActor.id);
  if (templates.length > 0) await canvas.scene.deleteEmbeddedDocuments("MeasuredTemplate", templates.map((t) => t.id));
}

const lastArg = args[args.length - 1];

// Check when applying the effect - if the token is not the caster and it IS the tokens turn they take damage
if (args[0] === "on" && args[1] !== lastArg.tokenId && lastArg.tokenId === game.combat?.current.tokenId) {
  const sourceItem = await fromUuid(lastArg.origin);
  const tokenOrActor = await fromUuid(lastArg.actorUuid);
  const theActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
  const DAEItem = lastArg.efData.flags.dae.itemData;
  const damageType = getProperty(DAEItem, "flags.ddbimporter.damageType") || "radiant";

  const itemData = mergeObject(
    duplicate(sourceItem.data),
    {
      type: "weapon",
      effects: [],
      flags: {
        "midi-qol": {
          noProvokeReaction: true, // no reactions triggered
          onUseMacroName: null, //
        },
      },
      data: {
        equipped: true,
        actionType: "save",
        save: { dc: Number.parseInt(args[3]), ability: "wis", scaling: "flat" },
        damage: { parts: [[`${args[2]}d8`, damageType]] },
        "target.type": "self",
        components: { concentration: false, material: false, ritual: false, somatic: false, value: "", vocal: false },
        duration: { units: "inst", value: undefined },
        weaponType: "improv",
      },
    },
    { overwrite: true, inlace: true, insertKeys: true, insertValues: true }
  );
  itemData.data.target.type = "self";
  setProperty(itemData.flags, "autoanimations.killAnim", true);
  const item = new CONFIG.Item.documentClass(itemData, { parent: theActor });
  const options = { showFullCard: false, createWorkflow: true, versatile: false, configureDialog: false };
  await MidiQOL.completeItemRoll(item, options);
}

if (!game.modules.get("dfreds-convenient-effects")?.active) {
  ui.notifications.error("Please enable the CE module");
  return;
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const DAEItem = lastArg.efData.flags.dae.itemData;
const saveData = DAEItem.data.save;

function effectAppliedAndActive(conditionName) {
  return targetActor.data.effects.some(
    (activeEffect) =>
      activeEffect?.data?.flags?.isConvenient &&
      activeEffect?.data?.label == conditionName &&
      !activeEffect?.data?.disabled
  );
}

async function eyebite(type) {
  for (let t of game.user.targets) {
    const flavor = `${CONFIG.DND5E.abilities["wis"]} DC${saveData.dc} ${DAEItem?.name || ""}`;
    const saveRoll = await targetActor.rollAbilitySave("wis", { flavor, fastFoward: true });
    if (saveRoll.total < saveData.dc) {
      ChatMessage.create({ content: `${t.name} failed the save with a ${saveRoll.total}` });
      switch (type) {
        case "asleep":
          game.dfreds.effectInterface.addEffect({ effectName: "Unconscious", uuid: t.actor.uuid });
          break;
        case "panicked":
          game.dfreds.effectInterface.addEffect({ effectName: "Frightened", uuid: t.actor.uuid });
          break;
        case "sickened":
          game.dfreds.effectInterface.addEffect({ effectName: "Poisoned", uuid: t.actor.uuid });
          break;
        // no default
      }
      DAE.setFlag(targetActor, "eyebiteSpell", type);
    } else {
      ChatMessage.create({ content: `${t.name} passed the save with a ${saveRoll.total}` });
    }
  }
}

function eyebiteDialog() {
  new Dialog({
    title: "Eyebite options",
    content: "<p>Target a token and select the effect</p>",
    buttons: {
      one: {
        label: "Asleep",
        callback: async () => await eyebite("asleep"),
      },
      two: {
        label: "Panicked",
        callback: async () => await eyebite("panicked"),
      },
      three: {
        label: "Sickened",
        callback: async () => await eyebite("sickened"),
      },
    },
  }).render(true);
}

if (args[0] === "on") {
  eyebiteDialog();
  ChatMessage.create({ content: `${targetActor.name} is blessed with Eyebite` });
}

if (args[0] === "each") {
  eyebiteDialog();
}

if (args[0] === "off") {
  const flag = await DAE.getFlag(targetActor, "eyebiteSpell");
  if (flag) {
    if (effectAppliedAndActive(flag, targetActor)) game.dfreds.effectInterface.removeEffect({ effectName: flag, uuid: targetActor.uuid });
    await DAE.unsetFlag(targetActor, "eyebiteSpell");
  }
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const weapons = targetActor.data.items.filter((i) => i.data.type === "weapon");

/**
 * For every str weapon, update the damage formulas to half the damage, set flag of original
 */
if (args[0] === "on") {
  weapons.forEach((weapon) => {
    if (weapon.abilityMod === "str") {
      const originalParts = duplicate(weapon.data.data.damage.parts);
      weapon.setFlag("world", "RayOfEnfeeblementSpell", originalParts);
      weapon.data.data.damage.parts.forEach((part) => {
        part[0] = `floor((${part[0]})/2)`;
      });
      weapon.update({ "data.damage.parts": weapon.data.data.damage.parts });
    }
  });
}

// Update weapons to old value
if (args[0] === "off") {
  weapons.forEach((weapon) => {
    const parts = weapon.getFlag("world", "RayOfEnfeeblementSpell");
    weapon.update({ "data.damage.parts": parts });
  });
}

