macro.command:
let actor;
let buzzed = "Buzzed";
let poisoned = "Alcohol Poisoning";
const speaker = ChatMessage.getSpeaker();
if (speaker.token)
    actor = game.actors.tokens[speaker.token];
actor ??= game.actors.get(speaker.actor);
if (!actor)
    return;

if (game.dfreds.effectInterface.hasEffectApplied(buzzed, actor.uuid)) {
    ChatMessage.create({
        user: game.user.id,
        speaker: ChatMessage.getSpeaker(),
        content: "Drinks to much brandy and turns a bit green...",
        type: CONST.CHAT_MESSAGE_TYPES.EMOTE
    });
    if (!game.dfreds.effectInterface.hasEffectApplied(poisoned, actor.uuid)) {
        game.dfreds.effectInterface.addEffect({ effectName: poisoned, uuid: actor.uuid });
    }
}
else {
    game.dfreds.effectInterface.addEffect({ effectName: buzzed, uuid: actor.uuid });
    var newHp = actor.data.data.attributes.hp.value + 1;
    var maxHp = actor.data.data.attributes.hp.max;
    var message = "Pops the cork on the brandy and takes a sip.  He gets a warm feeling";
    if (newHp < maxHp) {
        message +=  " and feels better.";
        ChatMessage.create({
            user: game.user.id,
            speaker: ChatMessage.getSpeaker(),
            content: "Pops the cork on the brandy and takes a sip.  He gets a warm feeling and feels better.",
            type: CONST.CHAT_MESSAGE_TYPES.OOC
        });
        actor.update({"data.attributes.hp.value": newHp});
    }
    else {
        message += ".";
    }
    ChatMessage.create({
        user: game.user.id,
        speaker: ChatMessage.getSpeaker(),
        content: message,
        type: CONST.CHAT_MESSAGE_TYPES.OOC
    });

}
await item.update({"data.quantity": (item.data.data.quantity - 1)});
if (item.data.data.quantity == 0) {
    item.delete();
}
