if (!game.modules.get("dfreds-convenient-effects")?.active) {
  ui.notifications.error("Please enable the CE module");
  return;
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const DAEItem = lastArg.efData.flags.dae.itemData;
const saveData = DAEItem.data.save;

function effectAppliedAndActive(conditionName) {
  return targetActor.data.effects.some(
    (activeEffect) =>
      activeEffect?.data?.flags?.isConvenient &&
      activeEffect?.data?.label == conditionName &&
      !activeEffect?.data?.disabled
  );
}

async function eyebite(type) {
  for (let t of game.user.targets) {
    const flavor = `${CONFIG.DND5E.abilities["wis"]} DC${saveData.dc} ${DAEItem?.name || ""}`;
    const saveRoll = await targetActor.rollAbilitySave("wis", { flavor, fastFoward: true });
    if (saveRoll.total < saveData.dc) {
      ChatMessage.create({ content: `${t.name} failed the save with a ${saveRoll.total}` });
      switch (type) {
        case "asleep":
          game.dfreds.effectInterface.addEffect({ effectName: "Unconscious", uuid: t.actor.uuid });
          break;
        case "panicked":
          game.dfreds.effectInterface.addEffect({ effectName: "Frightened", uuid: t.actor.uuid });
          break;
        case "sickened":
          game.dfreds.effectInterface.addEffect({ effectName: "Poisoned", uuid: t.actor.uuid });
          break;
        // no default
      }
      DAE.setFlag(targetActor, "eyebiteSpell", type);
    } else {
      ChatMessage.create({ content: `${t.name} passed the save with a ${saveRoll.total}` });
    }
  }
}

function eyebiteDialog() {
  new Dialog({
    title: "Eyebite options",
    content: "<p>Target a token and select the effect</p>",
    buttons: {
      one: {
        label: "Asleep",
        callback: async () => await eyebite("asleep"),
      },
      two: {
        label: "Panicked",
        callback: async () => await eyebite("panicked"),
      },
      three: {
        label: "Sickened",
        callback: async () => await eyebite("sickened"),
      },
    },
  }).render(true);
}

if (args[0] === "on") {
  eyebiteDialog();
  ChatMessage.create({ content: `${targetActor.name} is blessed with Eyebite` });
}

if (args[0] === "each") {
  eyebiteDialog();
}

if (args[0] === "off") {
  const flag = await DAE.getFlag(targetActor, "eyebiteSpell");
  if (flag) {
    if (effectAppliedAndActive(flag, targetActor)) game.dfreds.effectInterface.removeEffect({ effectName: flag, uuid: targetActor.uuid });
    await DAE.unsetFlag(targetActor, "eyebiteSpell");
  }
}

const lastArg = args[args.length - 1];
const tokenOrActor = await fromUuid(lastArg.actorUuid);
const targetActor = tokenOrActor.actor ? tokenOrActor.actor : tokenOrActor;
const weapons = targetActor.data.items.filter((i) => i.data.type === "weapon");

/**
 * For every str weapon, update the damage formulas to half the damage, set flag of original
 */
if (args[0] === "on") {
  weapons.forEach((weapon) => {
    if (weapon.abilityMod === "str") {
      const originalParts = duplicate(weapon.data.data.damage.parts);
      weapon.setFlag("world", "RayOfEnfeeblementSpell", originalParts);
      weapon.data.data.damage.parts.forEach((part) => {
        part[0] = `floor((${part[0]})/2)`;
      });
      weapon.update({ "data.damage.parts": weapon.data.data.damage.parts });
    }
  });
}

// Update weapons to old value
if (args[0] === "off") {
  weapons.forEach((weapon) => {
    const parts = weapon.getFlag("world", "RayOfEnfeeblementSpell");
    weapon.update({ "data.damage.parts": parts });
  });
}

// based on @ccjmk and @crymic macro for sleep. Gets targets and ignores those who are immune to sleep.
// uses convinient effects
// Midi-qol "On Use"

async function wait(ms) { return new Promise(resolve => { setTimeout(resolve, ms); }); }
const sleepHp = await args[0].damageTotal;
const condition = "Unconscious";
console.log(`Sleep Spell => Available HP Pool [${sleepHp}] points`);
const targets = await args[0].targets.filter((i) => i.actor.data.data.attributes.hp.value != 0 && !i.actor.effects.find(x => x.data.label === condition)).sort((a, b) => canvas.tokens.get(a.id).actor.data.data.attributes.hp.value < canvas.tokens.get(b.id).actor.data.data.attributes.hp.value ? -1 : 1);
let remainingSleepHp = sleepHp;
let sleepTarget = [];

for (let target of targets) {
    const findTarget = await canvas.tokens.get(target.id);
    const immuneType = findTarget.actor.data.type === "character" ? ["undead", "construct"].some(race => (findTarget.actor.data.data.details.race || "").toLowerCase().includes(race)) : ["undead", "construct"].some(value => (findTarget.actor.data.data.details.type.value || "").toLowerCase().includes(value));
    const immuneCI = findTarget.actor.data.data.traits.ci.custom.includes("Sleep");
    const sleeping = findTarget.actor.effects.find((i) => i.data.label === condition);
    const targetHpValue = findTarget.actor.data.data.attributes.hp.value;
    if ((immuneType) || (immuneCI) || (sleeping)) {
        console.log(`Sleep Results => Target: ${findTarget.name} | HP: ${targetHpValue} | Status: Resists`);
        sleepTarget.push(`<div class="midi-qol-flex-container"><div>Resists</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${findTarget.data.img}" width="30" height="30" style="border:0px"></div></div>`);
        continue;
    }
    if (remainingSleepHp >= targetHpValue) {
        remainingSleepHp -= targetHpValue;
        console.log(`Sleep Results => Target: ${findTarget.name} |  HP: ${targetHpValue} | HP Pool: ${remainingSleepHp} | Status: Slept`);
        sleepTarget.push(`<div class="midi-qol-flex-container"><div>Slept</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${findTarget.data.img}" width="30" height="30" style="border:0px"></div></div>`);
        const gameRound = game.combat ? game.combat.round : 0;
        const effectData = {
            label: "Sleep Spell",
            icon: "icons/svg/sleep.svg",
            origin: args[0].uuid,
            disabled: false,
            duration: { rounds: 10, seconds: 60, startRound: gameRound, startTime: game.time.worldTime },
            flags: { dae: { specialDuration: ["isDamaged"] } },
            changes: [
              { key: "macro.CE", mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM, value: "Prone", priority: 20 },
              { key: "macro.CE", mode: CONST.ACTIVE_EFFECT_MODES.CUSTOM, value: "Unconscious", priority: 20 },
            ]
        };

        await MidiQOL.socket().executeAsGM("createEffects", { actorUuid: findTarget.actor.uuid, effects: [effectData] });
        continue;
    } else {
        console.log(`Sleep Results => Target: ${target.name} | HP: ${targetHpValue} | HP Pool: ${remainingSleepHp - targetHpValue} | Status: Missed`);
        sleepTarget.push(`<div class="midi-qol-flex-container"><div>misses</div><div class="midi-qol-target-npc midi-qol-target-name" id="${findTarget.id}"> ${findTarget.name}</div><div><img src="${findTarget.data.img}" width="30" height="30" style="border:0px"></div></div>`);
    }
}
await wait(500);
const sleptResults = `<div><div class="midi-qol-nobox">${sleepTarget.join('')}</div></div>`;
const chatMessage = game.messages.get(args[0].itemCardId);
let content = duplicate(chatMessage.data.content);
const searchString = /<div class="midi-qol-hits-display">[\s\S]*<div class="end-midi-qol-hits-display">/g;
const replaceString = `<div class="midi-qol-hits-display"><div class="end-midi-qol-hits-display">${sleptResults}`;
content = await content.replace(searchString, replaceString);
await chatMessage.update({ content: content });

